package NestedIfElse;

public class NIEClass4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int marks = 90;
		
		if(marks>=35 && marks<50) {
			System.out.println("the student just passed");
		}
		   else if(marks>=50 && marks<60) {
 	              System.out.println("the student got second class");
		                 }
		

		              else if(marks>=60 && marks<90) {
			                  System.out.println("the student got first class");
		                }
		

		                     else if(marks>=90) {
			                 System.out.println("the student got distinction");
		                         }
		                      else
		                      {
			               System.out.println("the student got failed");
		                     }  
		
		
		
		
		
		
		
		
		
	}
	

}
