public class Centuryyear {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int year = 1900;
		
		if(year%100==0) {
			System.out.println("the given year is century year");
		}
		else {
			System.out.println("the given year is not a  century year");
		}
	}

}
