package ArrayListPack;

import java.util.ArrayList;

public class ALClass4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		ArrayList k = new ArrayList();
		
		k.add("parrot"); //0
		k.add(4565);       //1
		k.add(false);      //2
		k.add(true);         //3
		k.add('U');       //4
		k.add(13858L);      //5
		k.add(false);      //6
		k.add(4535);       //7
		k.add("cat");     //8
		
		System.out.println(k);
		
	System.out.println("the number of elements in the collection are: " +k.size());
	}

}
